CBA Add Setting.body:
[
    QGVAR(${1:settingName}),
    "${2:settingType}",
    [localize LSTRING(${3:title}), localize LSTRING(${4:description})],
    [format ["5th Cav %1", localize LSTRING(${5:category})], localize LSTRING(${6:subcategory})],
    [${5:valueInfo}],
    ${6:isGlobal},
    {
        ${7:scriptOnChange}
    },
    ${8:needRestart}
] call CBA_fnc_addSetting;
